def map_suit_to_str(suit: Suit):
    if suit == Suit.CLUBS:
        return 'c'
    if suit == Suit.DIAMONDS:
        return 'd'
    if suit == Suit.HEARTS:
        return 'h'
    return 's'

def map_rank_to_str(rank: Rank):
    if rank == Rank.ACE:
        return 'A'
    if rank == Rank.KING:
        return 'K'
    if rank == Rank.QUEEN:
        return 'Q'
    if rank == Rank.JACK:
        return 'J'
    if rank == Rank.TEN:
        return 'T'
    if rank == Rank.NINE:
        return '9'
    if rank == Rank.EIGHT:
        return '8'
    if rank == Rank.SEVEN:
        return '7'
    if rank == Rank.SIX:
        return '6'
    if rank == Rank.FIVE:
        return '5'
    if rank == Rank.FOUR:
        return '4'
    if rank == Rank.THREE:
        return '3'
    if rank == Rank.DEUCE:
        return '2'

def get_str_from_combo(combo: Combo):
    return map_rank_to_str(combo.first.rank) + map_suit_to_str(combo.first.suit) + map_rank_to_str(combo.second.rank) + map_suit_to_str(combo.second.suit)

